class DetailViewController: UIViewController {
    
    private let detailView: DetailView = DetailView()
    private let disposeBag = DisposeBag()
    
    private var viewModel: DetailViewModel!
    
    weak var flowDelegate: ListFlowDelegate?
    weak var actionDelegate: StartableViewController?
    
    init(with viewModel: DetailViewModel) {
        self.viewModel = viewModel
        super.init(nibName: nil, bundle: nil)
        
        self.detailView.webView.uiDelegate = self
        self.detailView.webView.navigationDelegate = self
        
    }
    
    @available(*, unavailable)
    required init?(coder aDecoder: NSCoder) { fatalError("init(coder:) has not been implemented") }
    
    override func loadView() {
        super.loadView()
        self.view = detailView
    }
    
    override func viewDidLoad() {
        super.viewDidLoad()
        viewModel
            .new
            .asObserver()
            .subscribe(onNext: { (new) in
                print(new.url)
//                if let url = URL(string: new.url) {
//                    let request = URLRequest(url: url)
//                    self.detailView
//                        .webView.load(request)
//                }
            })
            .disposed(by: disposeBag)
        
        viewModel
            .content
            .observeOn(MainScheduler.instance)
            .subscribe(onNext: { (contentString) in
                self.detailView
                    .webView
                    .loadHTMLString((Sttring.header + contentString + Sttring.afterFooter), baseURL: nil)
            })
            .disposed(by: disposeBag)
        
        actionDelegate?.startViewController()
    }
}

extension DetailViewController: WKUIDelegate, WKNavigationDelegate {

    
    func webView(_ webView: WKWebView, decidePolicyFor navigationAction: WKNavigationAction, decisionHandler: @escaping (WKNavigationActionPolicy) -> Void) {
        if navigationAction.navigationType == WKNavigationType.linkActivated {
            print("link")
            
            decisionHandler(WKNavigationActionPolicy.cancel)
            resolve(url: (navigationAction.request.url?.absoluteString)!)
//            UIApplication.shared.openURL(URL(string: "http://www.google.com")!)

        } else {
            
            print("no link")
            decisionHandler(.allow)
        }
    }
    
    private func resolve(url: String) {
        if url.contains("http") {
            open(url: url)
        } else {
            open(url: "http://www.vagalume.com.br/\(url)")
        }
    }
    
    private func open(url: String) {
           UIApplication.shared.openURL(URL(string: url)!)
    }
    
    
    
    func openCustomApp(urlScheme:String, additional_info:String){
        
        if let requestUrl:NSURL = NSURL(string:"\(urlScheme)"+"\(additional_info)") {
            let application:UIApplication = UIApplication.shared
            if application.canOpenURL(requestUrl as URL) {
                application.openURL(requestUrl as URL)
            }
        }
    }
}

extension URLRequest {
    var isHttpLink: Bool {
        return self.url?.scheme?.contains("http") ?? false
    }
}

